import Header from "../Header/Header";
import { useState } from "react";
import { CurrentUserContext } from "../../contexts/CurrentUserContext";
import { useContext } from "react";

const Profile = ({
  loggedIn,
  onSignOut,
  onProfileSubmit,
  errorStatus,
  setErrorStatus,
  errorText,
}) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [isPushed, setIsPushed] = useState(false);
  const currentUser = useContext(CurrentUserContext);

  const [nameValidation, setNameValidation] = useState({
    nameValidationMessage: "",
    isNameValid: false,
  });
  const [emailValidation, setEmailValidation] = useState({
    emailValidationMessage: "",
    isEmailValid: false,
  });

  const handelNameChange = (e) => {
    setName(e.target.value);

    const { validationMessage } = e.target;

    setNameValidation({
      nameValidationMessage: validationMessage,
      isNameValid: e.target.validity.valid,
    });
    setErrorStatus(false);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);

    const { validationMessage } = e.target;

    setEmailValidation({
      emailValidationMessage: validationMessage,
      isEmailValid: e.target.validity.valid,
    });
  };

  function editButtonHandleClick() {
    setIsPushed(!isPushed);
  }

  function handelFormSubmit(e) {
    e.preventDefault();
    onProfileSubmit(name, email);
  }

  return (
    <>
      <Header loggedIn={loggedIn} />
      <form className="profile" onSubmit={handelFormSubmit}>
        <h2 className="profile__title">{`Привет, ${currentUser.name}!`}</h2>
        {!isPushed ? (
          <>
            <div className="profile__info">
              <p className="profile__info-text">Имя</p>
              <p className="profile__info-text">{currentUser.name}</p>
            </div>
            <div className="profile__info">
              <p className="profile__info-text">E-mail</p>
              <p className="profile__info-text">{currentUser.email}</p>
            </div>
          </>
        ) : (
          <>
            <div className="profile__info">
              <p className="profile__info-text">Имя</p>
              <div className="profile__info-input">
                <input
                  className="signform__field-input"
                  type="text"
                  value={name}
                  placeholder={currentUser.name}
                  name="profileName"
                  id="profile-name-input"
                  pattern="^[A-Za-zА-Яа-яЁё\-\s]+$"
                  onChange={handelNameChange}
                  autoComplete="off"
                  noValidate
                  required
                />
                <span
                  className={`signform__field-error profile-email-input-error ${
                    !nameValidation.isNameValid
                      ? "signform__input-error_active"
                      : ""
                  }`}
                >
                  {nameValidation.nameValidationMessage}
                </span>
              </div>
            </div>
            <div className="profile__info">
              <p className="profile__info-text">E-mail</p>
              <div className="profile__info-input">
                <input
                  className="signform__field-input"
                  type="email"
                  value={email || ""}
                  placeholder={currentUser.email}
                  name="profileEmail"
                  id="profile-email-input"
                  pattern="[a-z0-9]+@[a-z]+\.[a-z]{2,3}"
                  onChange={handleEmailChange}
                  autoComplete="off"
                  noValidate
                  required
                />
                <span
                  className={`signform__field-error profile-email-input-error ${
                    !emailValidation.isEmailValid
                      ? "signform__input-error_active"
                      : ""
                  }`}
                >
                  {emailValidation.emailValidationMessage}
                </span>
              </div>
            </div>
          </>
        )}
        {errorStatus ? (
          <span className="signform__server-error">{errorText}</span>
        ) : (
          <></>
        )}
        <button
          onClick={editButtonHandleClick}
          type={isPushed ? "submit" : "button"}
          className="profile__btn profile__btn-edit"
          disabled={
            name === currentUser.name && email === currentUser.email && isPushed
              ? true
              : false
          }
        >
          {isPushed ? "Сохранить" : errorStatus ? "Сохранить" : "Редактировать"}
        </button>
        <button
          type="button"
          onClick={onSignOut}
          className="profile__btn profile__btn-out"
        >
          Выйти из аккаунта
        </button>
      </form>
    </>
  );
};

export default Profile;
