/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable no-underscore-dangle */
import React, { useState, useEffect } from "react";
import { Route, Switch, useHistory } from "react-router-dom";
import Main from "../Main/Main";
import Movies from "../Movies/Movies";
import SaveMovies from "../SavedMovies/SavedMovies";
import Profile from "../Profile/Profile";
import Login from "../Login/Login";
import Register from "../Register/Register";
import NotFoundPage from "../NotFoundPage/NotFoundPage";
import mainApi from "../../utils/MainApi";
import { CurrentUserContext } from "../../contexts/CurrentUserContext";
import ProtectedRoute from "../ProtectedRoute/ProtectedRoute";
import { CONFLICT_ERROR, UNAUTHORIZED_ERROR } from "../../utils/constants";

function App() {
  const [loggedIn, setLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState("");
  const [errorStatus, setErrorStatus] = useState(false);
  const [errorText, setErrorText] = useState("");
  const [successStatus, setSuccessStatus] = useState(false);
  const history = useHistory();
  const [token, setToken] = useState("");

  useEffect(() => {
    function tokenCheck() {
      if (localStorage.getItem("token")) {
        const token = localStorage.getItem("token");
        setLoggedIn(true);
        history.push("/movies");
      }
    }
    tokenCheck();
  }, [history, token]);

  function makeLogin(email, password) {
    mainApi
      .makeSignIn(email, password)
      .then((res) => {
        if (res.token) {
          localStorage.setItem("token", res.token);
          setToken(localStorage.getItem("token"));
          setLoggedIn(true);
          history.push("/movies");
          console.log(currentUser);
        }
      })
      .catch((err) => {
        if (err.status === UNAUTHORIZED_ERROR) {
          setErrorStatus(true);
          setErrorText("Неправильный логин или пароль.");
        }
      });
  }

  function makeRegister(name, email, password) {
    mainApi.makeSignUp(name, email, password).then((data) => {
      setCurrentUser(data);
      makeLogin(email, password);
    });
  }

  function handleProfileSubmit(name, email) {
    mainApi
      .updateUserInfo(name, email)
      .then((data) => {
        setCurrentUser(data);
        setErrorStatus(false);
        setSuccessStatus(true);
      })
      .catch((err) => {
        console.log(err);
        setErrorStatus(true);
        setErrorText("При обновлении профиля произошла ошибка.");
      });
  }

  function handleSignOut() {
    localStorage.removeItem("token");
    setLoggedIn(false);
    localStorage.removeItem("savedMovies");
    localStorage.removeItem("query");
    localStorage.removeItem("isShort");
    history.push("/");
  }

  return (
    <div className="page">
      <CurrentUserContext.Provider value={currentUser}>
        <Switch>
          <Route path="/sign-in">
            <Register
              errorStatus={errorStatus}
              setErrorStatus={setErrorStatus}
              errorText={errorText}
              onSubmit={makeRegister}
            />
          </Route>
          <Route path="/sign-up">
            <Login
              errorStatus={errorStatus}
              setErrorStatus={setErrorStatus}
              errorText={errorText}
              onSubmit={makeLogin}
            />
          </Route>
          <Route exact path="/">
            <Main loggedIn={loggedIn} />
          </Route>
          <ProtectedRoute loggedIn={loggedIn}>
            <Route path="/movies">
              <Movies loggedIn={loggedIn} />
            </Route>

            <Route path="/saved-movies">
              <SaveMovies loggedIn={loggedIn} />
            </Route>
            <Route path="/profile">
              <Profile
                loggedIn={loggedIn}
                onProfileSubmit={handleProfileSubmit}
                onSignOut={handleSignOut}
                errorStatus={errorStatus}
                errorText={errorText}
                successStatus={successStatus}
                setSuccessStatus={setSuccessStatus}
              />
            </Route>
          </ProtectedRoute>
          <Route path="*">
            <NotFoundPage loggedIn={loggedIn} />
          </Route>
        </Switch>
      </CurrentUserContext.Provider>
    </div>
  );
}

export default App;
