import { Link } from "react-router-dom";
import { useState } from "react";

const PopupMenu = ({ loggedIn }) => {
  const [isOpen, setIsOpen] = useState(false);

  const HandleClickOpen = () => {
    setIsOpen(true);
  };

  const HandleClickClose = () => {
    setIsOpen(false);
    console.log("suck");
  };

  return (
    <div>
      <button className="popup__open" onClick={HandleClickOpen} />
      <div className={`popup ${isOpen && "popup_type_opened"}`}>
        <button
          onClick={HandleClickClose}
          className="popup__close"
          type="button"
        ></button>
        <Link className="popup__link" to="/">
          Главная
        </Link>
        {loggedIn ? (
          <>
            <Link className="popup__link" to="/sign-in">
              Регистрация
            </Link>
            <Link className="popup__link" to="/sign-up">
              Войти
            </Link>
          </>
        ) : (
          <>
            <Link className="popup__link" to="/movies">
              Фильмы
            </Link>
            <Link className="popup__link" to="/saved-movies">
              Сохраненные фильмы
            </Link>
            <Link className="popup__profile" to="/profile">
              Аккаунт
            </Link>
          </>
        )}
      </div>
    </div>
  );
};

export default PopupMenu;
