import MoviesCardList from "./MoviesCardList/MoviesCardList";
import SearchForm from "./SearchForm/SearchForm";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import { filterMovies, filterCheckedMovies } from "../../utils/moviesFilter";
import { useEffect, useState } from "react";
import mainApi from "../../utils/MainApi";
import movieApi from "../../utils/MoviesApi";
import useWindowSize from "../../utils/useWindowSize";
import {
  SmallDisplay,
  MediumDisplay,
  LargeDisplay,
} from "../../utils/constants";
import moviesApi from "../../utils/MoviesApi";

const savedMoviesSorted = (movies, query, isShort) => {
  const savedMoviesKey = "savedMovies";
  const savedMoviesInLocalStorage = localStorage.getItem(savedMoviesKey);

  const savedMovies = savedMoviesInLocalStorage
    ? JSON.parse(savedMoviesInLocalStorage)
    : [];

  const savedMoviesByQuery = savedMovies.find(
    (i) => i.query === query && i.isShort === isShort
  );
  if (savedMoviesByQuery && savedMoviesByQuery.list.length !== 0) {
    console.log("Загружено из LocalStorage");
    return savedMoviesByQuery.list;
  }

  const moviesFromApi = isShort
    ? filterCheckedMovies(movies, query)
    : filterMovies(movies, query);

  localStorage.setItem(savedMoviesKey, [
    { query, isShort, list: moviesFromApi },
  ]);

  console.log("Загружено с Bitmovies");
  return moviesFromApi;
};

const Movies = ({ loggedIn }) => {
  const [movies, setMovies] = useState([]);
  const [sortedMovies, setSortedMovies] = useState([]);
  const [isChecked, setIsChecked] = useState(
    localStorage.getItem("isShort") === true
  );
  const [isThereSortetMovies, setIsThereSortedMovies] = useState(true);
  const [savedMovies, setSavedMovies] = useState([]);
  const [searchText, setSearchText] = useState(
    localStorage.getItem("query") || ""
  );
  const [visible, setVisible] = useState(0);
  const [windowWidth] = useWindowSize();

  useEffect(() => {
    if (loggedIn) {
      Promise.all([mainApi.getSavedMovies(), moviesApi.getAllMovies()])
        .then((data) => {
          const [savedMovies, allMovies] = data;
          setIsThereSortedMovies(true);
          setSavedMovies(savedMovies.movies);
          setMovies(allMovies);
        })
        .catch((err) => {
          console.log(err);
        });
    }
  }, [loggedIn]);

  useEffect(() => {
    const sortedMoviesByQuery = savedMoviesSorted(
      movies,
      searchText,
      isChecked
    );
    if (sortedMoviesByQuery.length !== 0) {
      setSortedMovies(sortedMoviesByQuery);
    } else {
      setSortedMovies(sortedMoviesByQuery);
      setIsThereSortedMovies(false);
    }
    localStorage.setItem("query", sortedMoviesByQuery);
  });

  return (
    <div className="movies">
      <Header loggedIn={loggedIn} />
      <SearchForm />
      <MoviesCardList />
      <Footer />
    </div>
  );
};

export default Movies;
