import React from "react";
import { Link, Redirect } from "react-router-dom";
import PopupMenu from "./PopupMenu/PopupMenu";

const Navigation = ({ loggedIn }) => {
  return (
    <>
      {!loggedIn ? (
        <div className="navbar navbar__nouser">
          <Link
            className="navbar__link navbar__link-signin"
            Redirect="/sign-in"
          >
            Регистрация
          </Link>
          <Link className="navbar__link navbar__link-signup" to="/sign-up">
            Войти
          </Link>
        </div>
      ) : (
        <div className="navigation">
          <div className="navbar navbar_withuser">
            <div className="navbar__links">
              <Link className="navbar__link navbar__link-signin" to="/movies">
                Фильмы
              </Link>
              <Link
                className="navbar__link navbar__link-signin"
                to="/saved-movies"
              >
                Сохраненные фильмы
              </Link>
            </div>
            <Link className="navbar__profile" to="/profile">
              Аккаунт
            </Link>
          </div>
          <PopupMenu />
        </div>
      )}
    </>
  );
};

export default Navigation;
