import { Link } from "react-router-dom";

const PopupMenu = ({ loggedIn, onClose }) => {
  return (
    <div className="popup">
      <button className="popup__button" type="button"></button>
      <h3 className="popup__title">Главная</h3>
      {loggedIn ? (
        <>
          <Link className="popup__link" to="/sign-in">
            Регистрация
          </Link>
          <Link className="popup__link" to="/sign-up">
            Войти
          </Link>
        </>
      ) : (
        <>
          <Link className="popup__link" to="/movies">
            Фильмы
          </Link>
          <Link className="popup__link" to="/saved-movies">
            Сохраненные фильмы
          </Link>
          <Link className="popup__profile" to="/profile">
            Аккаунт
          </Link>
        </>
      )}
      <button onClick={onClose} className="popup__close" type="button"></button>
    </div>
  );
};

export default PopupMenu;
