import { NavLink } from "react-router-dom";
import { useState } from "react";
import Main from "../../Main/Main";

const PopupMenu = ({ loggedIn }) => {
  const [isOpen, setIsOpen] = useState(false);

  const HandleClickOpen = () => {
    setIsOpen(true);
  };

  const HandleClickClose = () => {
    setIsOpen(false);
    console.log("suck");
  };

  return (
    <div>
      <button className="popup__open" onClick={HandleClickOpen} />
      <div className={`popup ${isOpen && "popup_type_opened"}`}></div>
      <div className="popup__content">
        <button
          onClick={HandleClickClose}
          className="popup__close"
          type="button"
        ></button>
        <NavLink className="popup__link" to="/">
          Главная
        </NavLink>
        <NavLink className="popup__link" to="/movies">
          Фильмы
        </NavLink>
        <NavLink className="popup__link" to="/saved-movies">
          Сохраненные фильмы
        </NavLink>
        <NavLink className="popup__profile" to="/profile">
          Аккаунт
        </NavLink>
      </div>
      <div onClick={HandleClickClose} className="popup__overlay" />
    </div>
  );
};

export default PopupMenu;
