import { useState } from "react";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handelChangeEmail = (e) => {
    setEmail(e.target.value);
  };

  const handelChangePassword = (e) => {
    setPassword(e.target.value);
  };
  const buttonClassName = `signform__submit ${
    email !== "" && password !== "" ? "" : "signform__submit_disabled"
  }`;

  const handelSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <form className="signform" onSubmit={handelSubmit}>
      <h2 className="signform__title">Добро пожаловать!</h2>
      <div className="signform__field">
        <h5 className="signform__field-subtitle">Имя</h5>
        <input
          className="signform__field-input"
          type="name"
          name="name"
          onChange={handelChangeEmail}
          required
        />
        <span className="signform__field-error"></span>
      </div>
      <div className="signform__field">
        <h5 className="signform__field-subtitle">E-mail</h5>
        <input
          className="signform__field-input"
          type="email"
          name="email"
          onChange={handelChangeEmail}
          required
        />
        <span className="signform__field-error"></span>
      </div>
      <div className="signform__field">
        <h5 className="signform__field-subtitle">Пароль</h5>
        <input
          className="signform__field-input"
          type="password"
          name="password"
          onChange={handelChangePassword}
          required
        />
        <span className="signform__field-error">ЖЖЖООоопа</span>
      </div>
      <button className={buttonClassName} type="submit">
        Зарегистрироваться
      </button>
    </form>
  );
};

export default Login;
