import { useLocation } from "react-router-dom";
import { useState } from "react";
import FilmsCover from "../../../images/pic__COLOR_pic.jpg";

const MoviesCard = ({
  movie,
  savedMovies,
  onHandleBookmark,
  onDeleteMovie,
}) => {
  const location = useLocation();
  const [isLiked, setIsLiked] = useState(false);
  const isBookmarked = savedMovies.some((i) => i.nameRu === movie.nameRU);
  const movieBtnClassName = `movies-card__bookmark ${
    isBookmarked || isLiked ? "movie__button-save" : "movie__button-save_active"
  }`;

  return (
    <div className="movies-list">
      <li className="movie">
        <p className="movie__title">33 слова о дизайне</p>
        <p className="movie__duration">1ч 47м</p>
        <button className={movieBtnClassName} type="button"></button>
        <img className="movie__cover" src={FilmsCover} alt="Обложка"></img>
      </li>
    </div>
  );
};

export default MoviesCard;
