import { Link } from "react-router-dom";
import { useState, useEffect } from "react";

const PopupMenu = ({ loggedIn }) => {
  const [isOpen, setIsOpen] = useState(false);
  function onClose() {
    setIsPopupOpen(false);
  }

  useEffect(() => {
    const handleEscClose = (e) => {
      if (e.key === "Escape") {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener("keydown", handleEscClose);
    }
    return () => {
      document.removeEventListener("keydown", handleEscClose);
    };
  }, [isOpen, onClose]);

  return (
    <div className="popup">
      <button
        className="popup__button"
        type="button"
        onClick={isPopupOpen}
      ></button>
      <h3 className="popup__title">Главная</h3>
      {loggedIn ? (
        <>
          <Link className="popup__link" to="/sign-in">
            Регистрация
          </Link>
          <Link className="popup__link" to="/sign-up">
            Войти
          </Link>
        </>
      ) : (
        <>
          <Link className="popup__link" to="/movies">
            Фильмы
          </Link>
          <Link className="popup__link" to="/saved-movies">
            Сохраненные фильмы
          </Link>
          <Link className="popup__profile" to="/profile">
            Аккаунт
          </Link>
        </>
      )}
      <button onClick={onClose} className="popup__close" type="button"></button>
      <div onClick={onClose} className="popup__overlay" />
    </div>
  );
};

export default PopupMenu;
